CREATE TABLE public."Containers"
(
    "Id" INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
    "DockerContainerId" TEXT,
    "Name" TEXT,
    "Created" TIMESTAMP WITHOUT TIME ZONE,
    "State" TEXT,
    CONSTRAINT "PK_Containers" PRIMARY KEY ("Id")
);

CREATE TABLE public."ContainerStats"
(
    "Id" INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
    "StatDate" TIMESTAMP WITHOUT TIME ZONE,
    "CPUUsage" DOUBLE PRECISION,
    "MemoryUsage" DOUBLE PRECISION,
    "MemoryUsageMax" DOUBLE PRECISION,
    "NetworkUsage" DOUBLE PRECISION,
    "ContainerId" INTEGER,
    CONSTRAINT "PK_ContainerStats" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_ContainerStats_Containers_ContainerId" FOREIGN KEY ("ContainerId")
        REFERENCES public."Containers" ("Id") MATCH SIMPLE
        ON UPDATE NO ACTION
        ON DELETE RESTRICT
);